import { gql } from "@apollo/client";
import client from "./index";

class ErrorLowConfidence extends Error {
  constructor() {
    super();
    Error.captureStackTrace(this, this.constructor);
  }
}

// ActionProvider starter code
class ActionProvider {
  
  constructor(createChatbotMessage, setStateFunc, createClientMessage) {
    this.createChatbotMessage = createChatbotMessage;
    this.setState = setStateFunc;
    this.createClientMessage = createClientMessage;
  }

  async searchNlusByName(intent, entity, role, trait) {
    let data = client
      .query({
        query: gql`
          query Nlus($intent: String!, $entity: String!, $role: String, $trait: String!) {
            nlus(intent: $intent, entity: $entity, role: $role, trait: $trait) {
              intent {
                text
              }
              entity {
                text
              }
              role {
                text
              }
              trait {
                text
              }
            }
          }
        `,
        variables: { intent: intent, entity: entity, role: role, trait: trait }
      })
      .then(({ loading, error, data }) => { return data });

    return data;

  }

  async greet(respuesta) {
    try {
      const intent = respuesta.intents[0].name;
      const conf_intent = respuesta.intents[0].confidence;
      const ent = Object.values(Object.values(respuesta.entities)[0])[0].name;
      const ent_rol = Object.values(Object.values(respuesta.entities)[0])[0].role;
      const conf_ent = bject.values(Object.values(respuesta.entities)[0])[0].confidence;
      const trai = Object.values(Object.values(respuesta.traits)[0])[0].value;
      const conf_trai = Object.values(Object.values(respuesta.traits)[0])[0].confidence;

      let text_intent = "";
      let text_entity = "";
      let text_role = "";
      let text_trait = "";

      if (conf_intent < 0.7 || conf_ent < 0.7 || conf_trai < 0.7) {
        throw new ErrorLowConfidence();
      }

      let role = null
      if (ent_rol !== ent) {
        role = ent_rol
      }
      await this.searchNlusByName(intent, ent, role, trai).then((data) => {
        let response = data.nlus
        text_intent = response.intent.text;
        text_entity = response.entity.text;
        if (response.role){
          text_role = response.role.text;
        }
        text_trait = response.trait.text;
      });

      // mensaje = a+b+c+d
      const mensaje =
        text_intent +
        " - " +
        text_entity +
        " - " +
        text_role +
        " - " +
        text_trait;

      const greetingMessage = this.createChatbotMessage(mensaje);
      this.updateChatbotState(greetingMessage);
    } catch (e) {
      console.log(e);
      this.updateChatbotState(
        this.createChatbotMessage("No entiendo tu pregunta")
      );
    }
  } //fin funcion

  updateChatbotState(message) {
    // NOTE: This function is set in the constructor, and is passed in
    // from the top level Chatbot component. The setState function here
    // actually manipulates the top level state of the Chatbot, so it's
    // important that we make sure that we preserve the previous state.
    this.setState((prevState) => ({
      ...prevState,
      messages: [...prevState.messages, message]
    }));
  }
}

export default ActionProvider;
